import { useDispatch, useSelector } from 'react-redux';
import { addTodo, removeTodo } from '../features/todo/manageTodo';

export const TodoList = () => {
  const todos = useSelector((state) => state.manageTodo.todos);
  const dispatch = useDispatch();
  return (
    <div>
      <span className="p-1 mr-3 bg-blue-500 rounded hover:bg-blue-600" onClick={() => dispatch(addTodo())}>
        <button className="text-sm text-white font-bold">할 일 추가</button>
      </span>
      <span className="p-1 bg-red-500 rounded hover:bg-red-600" onClick={() => dispatch(removeTodo())}>
        <button className="text-sm text-white font-bold">할 일 제거</button>
      </span>
      <div id="todo-list" className="flex mt-5">
        <input type="checkbox" name="" id="" onChange={(e) => console.log(e)} />
        {todos.map((val) => (
          <div className="border border-solid border-black rounded-lg p-3">
            <span className="mr-10">{val.id}</span>
            <span>{val.text}</span>
          </div>
        ))}
      </div>
    </div>
  );
};
