import { useEffect, useState } from 'react';
import { addBoard } from '../../features/blog/manageBlog';
import { useDispatch } from 'react-redux';
import { useNavigate, useParams } from 'react-router-dom';

export const BoardInput = ({ currentBoard }) => {
  const [title, setTitle] = useState('');
  const [textarea, setTextArea] = useState('');
  const dispatch = useDispatch();
  const navigate = useNavigate();

  useEffect(() => {
    if (currentBoard) {
      setTitle(currentBoard.title);
      setTextArea(currentBoard.description);
    }
  }, []);

  const handleSubmit = () => {
    const data = {
      title,
      description: textarea,
    };
    dispatch(addBoard(data));
    navigate('/');
  };

  return (
    <div className="w-full">
      <header className="my-3">
        <h1 className="text-xl font-bold">Title:</h1>
        <input
          type="text"
          className="w-[60%] py-1 pl-1 border border-gray-400 rounded shadow focus:outline-none"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
      </header>
      <div className="mb-5">
        <label htmlFor="" className="text-xl font-bold">
          Description:{' '}
        </label>
        <textarea
          className="w-full h-[400px] pl-1 appearance-none border border-gray-400 rounded shadow focus:outline-none"
          value={textarea}
          onChange={(e) => setTextArea(e.target.value)}
        ></textarea>
      </div>
      <span
        className="p-3 px-5 bg-blue-500 text-white font-bold rounded hover:bg-blue-600"
        onClick={() => handleSubmit()}
      >
        <button>Submit</button>
      </span>
    </div>
  );
};
