import { createSlice } from '@reduxjs/toolkit';

export const manageTodo = createSlice({
  name: 'manageTodo',
  initialState: {
    checked_todos: [],
    todos: [{ id: 1, text: '할 일' }],
  },
  reducers: {
    addTodo: (state, action) => {
      const todo = { id: action.payload.id, text: action.payload.text };
      state.todos = [...state.todos, todo];
    },
    removeTodo: (state, action) => {
      const newArr = [...state.checked_todos];
      for (const id of newArr) {
        if (id === action.payload.id) {
          return;
        } else {
          newArr.push(action.payload.id);
        }
      }
      return state.todos.filter((val) => val.id !== state.checked_todos);
    },
  },
});

export const { addTodo, removeTodo } = manageTodo.actions;

export default manageTodo.reducer;
