import { useDispatch, useSelector } from 'react-redux';

export const TodoList = () => {
  const todos = useSelector((state) => state.manageTodo.todos);
  const dispatch = useDispatch();
  return (
    <div>
      <span className="p-1 mr-3 bg-blue-500 rounded">
        <button className="text-sm text-white">할 일 추가</button>
      </span>
      <span className="p-1 bg-red-500 rounded">
        <button className="text-sm text-white">할 일 제거</button>
      </span>
    </div>
  );
};
