import { useDispatch, useSelector } from 'react-redux';
import { addTodo, removeTodo } from '../features/todo/manageTodo';
import { useState } from 'react';

export const TodoList = () => {
  const todos = useSelector((state) => state.manageTodo.todos);
  const dispatch = useDispatch();
  const [willRemoveTodo, setWillRemoveTodos] = useState([]);

  const handleCheckedTodos = (e, todo_id) => {
    setWillRemoveTodos((prevState) => {
      if (e.target.checked) {
        return [...prevState, todo_id];
      } else {
        return [...prevState].filter((val) => val.id === todo_id);
      }
    });
  };

  return (
    <div>
      <span className="p-1 mr-3 bg-blue-500 rounded hover:bg-blue-600" onClick={() => dispatch(addTodo())}>
        <button className="text-sm text-white font-bold">할 일 추가</button>
      </span>
      <span className="p-1 bg-red-500 rounded hover:bg-red-600" onClick={() => dispatch(removeTodo())}>
        <button className="text-sm text-white font-bold">할 일 제거</button>
      </span>
      <div id="todo-list" className="mt-5">
        {todos.map((val) => (
          <div className="flex justify-between p-3 border border-solid border-gray-400 rounded shadow">
            <input type="checkbox" name="" id="" onChange={(e) => handleCheckedTodos(e, val.id)} />
            <span className="mr-10 text-sm">{val.id}</span>
            <span className="text-sm">{val.text}</span>
          </div>
        ))}
      </div>
    </div>
  );
};
