import { createSlice } from '@reduxjs/toolkit';

export const Blog = createSlice({
  name: 'manageBlog',
  initialState: {
    id: 1,
    title: '',
    description: '',
    boardsList: [],
    currentBoard: null,
  },
  reducers: {
    addBoard: (state, action) => {
      const { id, title, description } = action.payload;
      state.boardsList.push({ id, title, description });
      state.id += 1;
    },
    updateBoard: (state, action) => {
      const { id, title, description } = action.payload;
      const index = state.boardsList.find((board) => board.id === action.payload);
      const board = state.boardsList.filter((board) => board.id === index);
      const newBoard = { ...board, title, description, id };
      return newBoard;
    },
    removeBoard: (state, action) => {
      const index = state.boardsList.findIndex((board) => board.id === action.payload);
      if (index !== -1) {
        state.boardsList.splice(index, 1);
      }
    },
  },
});

export const { addBoard, updateBoard, removeBoard } = Blog.actions;

export default Blog.reducer;
