import { useDispatch, useSelector } from 'react-redux';
import { removeTodo } from '../features/todo/manageTodo';

export const TodoList = () => {
  const todos = useSelector((state) => state.manageTodo.todos);
  const dispatch = useDispatch();

  const handleAddTodo = () => {};

  return (
    <div>
      <span className="p-1 mr-3 bg-blue-500 rounded hover:bg-blue-600" onClick={() => handleAddTodo()}>
        <button className="text-sm text-white font-bold">할 일 추가</button>
      </span>

      <div id="todo-list" className="mt-5">
        {todos.map((val) => (
          <div className="flex justify-between items-center p-3 border border-solid border-gray-400 rounded shadow">
            <input type="checkbox" name="" id="" onChange={(e) => console.log(e)} />
            <span className="mr-10 text-xs">{val.id}</span>
            <span className="text-xs">{val.text}</span>
            <span className="bg-red-500 rounded hover:bg-red-600" onClick={() => dispatch(removeTodo(val.id))}>
              <button className="text-[0.6rem] text-white font-bold">할 일 제거</button>
            </span>
          </div>
        ))}
      </div>
    </div>
  );
};
