import { createSlice, nanoid } from '@reduxjs/toolkit';

export const manageTodo = createSlice({
  name: 'manageTodo',
  initialState: {
    checked_todos: [],
    todos: [{ id: 1, text: '할 일' }],
  },
  reducers: {
    addTodo: {
      reducer(state, action) {
        const { count, todoText } = action.payload;
        state.todos.push({ id: count, text: todoText });
      },
      prepare(data) {
        const { todoText, count } = data;
        return { payload: { id: count, text: todoText } };
      },
    },
    removeTodo(state, action) {
      const index = state.todos.findIndex((todo) => todo.id === action.payload);
      if (index !== -1) {
        state.todos.splice(index, 1);
      }
    },
  },
});

export const { addTodo, removeTodo } = manageTodo.actions;

export default manageTodo.reducer;
