import { useDispatch, useSelector } from 'react-redux';

export const TodoList = () => {
  const todos = useSelector((state) => state.manageTodo.todos);
  const dispatch = useDispatch();
  return (
    <div>
      <span className="p-1 mr-3 bg-blue-500 rounded hover:bg-blue-600">
        <button className="text-sm text-white font-bold">할 일 추가</button>
      </span>
      <span className="p-1 bg-red-500 rounded hover:bg-red-600">
        <button className="text-sm text-white font-bold">할 일 제거</button>
      </span>
      <div id="todo-list">
        {todos.map((val) => (
          <div>
            <span>{val.id}</span>
            <span>{val.text}</span>
          </div>
        ))}
      </div>
    </div>
  );
};
