import { createSlice } from '@reduxjs/toolkit';
import { fetchBoardsList } from './blogSlice';

export const Blog = createSlice({
  name: 'manageBlog',
  initialState: {
    id: 1,
    title: '',
    description: '',
    boardsList: [],
    currentBoard: null,
  },
  reducers: {
    selectBoard: (state, action) => {
      // 이미 board가 선택되어있던 경우
      if (state.currentBoard === action.payload) {
        state.currentBoard = null;
        // 아무것도 선택되어 있지 않았거나 다른 board가 선택되어있던 경우
      } else {
        state.currentBoard = action.payload;
      }
    },
    updateBtn: (state, action) => {
      if (state.currentBoard) {
        state.id = state.currentBoard.id;
        state.title = state.currentBoard.title;
        state.description = state.currentBoard.description;
      } else {
        alert('변경하실 게시물을 클릭해주세요!');
      }
    },
    updateBoard: (state, action) => {
      const { id, title, description } = action.payload;
      const index = state.boardsList.findIndex((val) => val.id === id);
      if (index !== -1) {
        state.boardsList[index] = { ...state.boardsList[index], title, description };
      }
    },
    removeBoard: (state, action) => {
      const index = state.boardsList.findIndex((board) => board.id === action.payload);
      if (index !== -1) {
        state.boardsList.splice(index, 1);
      }
    },
  },
  extraReducers: (builder) => {
    builder.addCase(fetchBoardsList.fulfilled, (state, action) => {
      state.boardsList = action.payload;
    });
  },
});

export const { addBoard, updateBtn, updateBoard, removeBoard, selectBoard } = Blog.actions;

export default Blog.reducer;
