        <h1>Title:</h1>
        <input type="text" className="w-full border border-gray-400 rounded shadow" />
      </header>
      <div>
        <label htmlFor="">Description: </label>
        <textarea className="w-full py-3 appearance-none border border-gray-400 rounded shadow"></textarea>
      </div>
    </div>
  );
};
