import { useState } from 'react';

export const BoardInput = () => {
  return (
    <div className="w-full">
      <header className="my-3">
        <h1 className="text-xl font-bold">Title:</h1>
        <input type="text" className="w-[60%] py-1 border border-gray-400 rounded shadow focus:outline-none" />
      </header>
      <div className="mb-5">
        <label htmlFor="" className="text-xl font-bold">
          Description:{' '}
        </label>
        <textarea className="w-full h-[400px] appearance-none border border-gray-400 rounded shadow focus:outline-none"></textarea>
      </div>
      <span className="p-3 bg-blue-500 text-white font-bold">
        <button>Submit</button>
      </span>
    </div>
  );
};
